import { Component, OnInit } from '@angular/core';
import { Order, OrderItem } from 'src/app/models/order';
import { OrderService } from 'src/app/services/order.service';
@Component({
  selector: 'app-order-form',
  templateUrl: './order-form.component.html',
  styleUrls: ['./order-form.component.css']
})
export class OrderFormComponent {

  constructor(private orderService: OrderService) {}

  submitForm() {
    // Create an OrderDto object with form data
    const newOrder: Order = {
      status: 'New',
      totalCost: 0,
      orderDate: new Date(),
      // Add other properties
    };

    // Call the service method to create the order
    this.orderService.createOrder(newOrder).subscribe(() => {
      // Order created successfully, navigate to the orders list
      // You can use Angular Router to navigate to the desired page
    });
  }
}
