import { Component, OnInit } from '@angular/core';
import { Customer } from 'src/app/models/customer';
import { Order, CreateOrderDto, OrderItem } from 'src/app/models/order';
import { Product } from 'src/app/models/product';
import { OrderService } from 'src/app/services/order.service';
@Component({
  selector: 'app-order-form',
  templateUrl: './order-form.component.html',
  styleUrls: ['./order-form.component.css']
})
export class OrderFormComponent implements OnInit {

  order: CreateOrderDto = {
    id:0,
    status:0,
    orderDate: new Date() ,
    customerId: 0,    
    customerAddress:'',
    orderItems: []
  };
  orderItems: OrderItem[] = [];
  customers: Customer[] =[]; 
  products: Product[] = []; 
  totalCost:number = 0;

  selectedCustomerId :number =0;
  constructor(private orderService: OrderService) {}



  ngOnInit() {
    // for visualization
    this.products = [
      {
        id: 1,
        name: 'Product 1',
        availableQuantity: 10,
        size: 100,
        price: 50.0,
        description: 'Description for Product 1',
        categoryId: 1,
        createdDate: new Date()
      },
      {
        id: 2,
        name: 'Product 2',
        availableQuantity: 15,
        size: 150,
        price: 75.0,
        description: 'Description for Product 2',
        categoryId: 2,
        createdDate: new Date()
      },
      {
        id: 3,
        name: 'Product 3',
        availableQuantity: 20,
        size: 200,
        price: 100.0,
        description: 'Description for Product 3',
        categoryId: 1,
        createdDate: new Date()
      }
    ];
    this.customers =[{
      id: 1,
      name: 'Customer A',
      address: 'Address A',
      ordersCount: 5,
      totalOrderedCost: 500,
      createdDate: new Date()
    },
    {
      id: 2,
      name: 'Customer B',
      address: 'Address B',
      ordersCount: 10,
      totalOrderedCost: 1000,
      createdDate: new Date()
    },
    {
      id: 3,
      name: 'Customer C',
      address: 'Address C',
      ordersCount: 3,
      totalOrderedCost: 300,
      createdDate: new Date()
    }]
  }
  submitForm() {
    const selectedCustomer = this.customers.find(customer => customer.id === this.selectedCustomerId);
    if (selectedCustomer) {
    this.order.customerId = this.selectedCustomerId;
    this.order.customerAddress = selectedCustomer.address;

    this.order.status = Number(this.order.status);
    this.order.orderItems = [
      { id: 0, productId: 4, quantity: 1 },
      { id: 0, productId: 5, quantity: 3 }
    ];
    console.log(this.order);
    this.orderService.createOrder(this.order).subscribe(() => {

    });

  }}
  calculateTotalCost(){
    this.totalCost = this.products.reduce((sum, product) => sum+ product.price * product.availableQuantity,0)
  }
}
